    const selector = data.slice(0, 4)
    const input = data.slice(4)

    switch (selector.toString(&#x27;hex&#x27;)) {
      case &#x27;hash&#x27;:
        const hash = keccak256(input)
        return {
          returnValue: hash,
          executionGasUsed: 100n + BigInt(input.length),
